import { NextResponse } from 'next/server';
import { fetchStudentData } from '@/lib/api42';

/**
 * API handler for fetching student data
 */
export async function GET(request, { params }) {
  try {
    const { username } = params;

    // Get the specific data type from query params
    const searchParams = request.nextUrl.searchParams;
    const dataType = searchParams.get('dataType');

    // Fetch comprehensive student data
    const studentData = await fetchStudentData(username);

    // Return only specific data if requested
    if (dataType) {
      switch (dataType) {
        case 'skills':
          return NextResponse.json({
            login: studentData.login,
            displayName: studentData.displayName,
            image: studentData.image,
            skills: studentData.skills
          });

        case 'projects':
          return NextResponse.json({
            login: studentData.login,
            displayName: studentData.displayName,
            image: studentData.image,
            projects: studentData.projects
          });

        case 'coalition':
          return NextResponse.json({
            login: studentData.login,
            displayName: studentData.displayName,
            image: studentData.image,
            coalition: studentData.coalition
          });

        default:
          break;
      }
    }

    // Return the full student data by default
    return NextResponse.json(studentData);
  } catch (error) {
    console.error('API Error:', error);
    return NextResponse.json(
      { error: error.message || 'Failed to fetch student data' },
      { status: 500 }
    );
  }
}

import { NextResponse } from 'next/server';
import { fetchStudentData } from '@/lib/api42';
import { 
  generateSkillsBars, 
  generateSkillsRadar, 
  generateSkillsBadge 
} from '@/lib/generators/skillsGenerator';

/**
 * Cache control constants
 */
const CACHE_MAX_AGE = 60 * 60; // 1 hour
const STALE_WHILE_REVALIDATE = 60 * 60 * 24; // 1 day

/**
 * API handler for widget generation
 */
export async function GET(request, { params }) {
    try {
      // Await params before destructuring
      const resolvedParams = await Promise.resolve(params);
      const { type, username } = resolvedParams;

      const searchParams = request.nextUrl.searchParams;
      const theme = searchParams.get('theme') || 'dark';
      const skillName = searchParams.get('skill') || null;

    // Common headers
    const headers = {
      'Content-Type': 'image/svg+xml',
      'Cache-Control': `public, max-age=${CACHE_MAX_AGE}, stale-while-revalidate=${STALE_WHILE_REVALIDATE}`,
    };

    // Fetch student data
    const studentData = await fetchStudentData(username);

    // Generate the appropriate widget based on type
    let svgContent;

    switch (type) {
      case 'skills-bars':
        svgContent = generateSkillsBars(studentData, theme);
        break;

      case 'skills-radar':
        svgContent = generateSkillsRadar(studentData, theme);
        break;

      case 'skills-badge':
        svgContent = generateSkillsBadge(studentData, theme, skillName);
        break;

      default:
        return NextResponse.json(
          { error: 'Invalid widget type' },
          { status: 400 }
        );
    }

    return new NextResponse(svgContent, { headers });
  } catch (error) {
    console.error('Widget generation error:', error);

    // Return a simple error SVG
    const errorSvg = `
      <svg xmlns="http://www.w3.org/2000/svg" width="400" height="200" viewBox="0 0 400 200">
        <rect width="400" height="200" fill="#1e293b" rx="10" ry="10" />
        <text x="200" y="80" font-family="Arial, sans-serif" font-size="20" fill="#ef4444" text-anchor="middle">
          Error
        </text>
        <text x="200" y="120" font-family="Arial, sans-serif" font-size="16" fill="#ffffff" text-anchor="middle">
          ${error.message || 'Failed to generate widget'}
        </text>
      </svg>
    `;

    return new NextResponse(errorSvg, {
      headers: { 'Content-Type': 'image/svg+xml' },
      status: 500
    });
  }
}

