'use client';

import { useState } from 'react';
import Link from 'next/link';

export default function HomePage() {
  const [username, setUsername] = useState('');
  const [theme, setTheme] = useState('dark');
  const [widgetType, setWidgetType] = useState('skills-bars');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [previewUrl, setPreviewUrl] = useState('');
  const [embedCode, setEmbedCode] = useState('');
  const [showPreview, setShowPreview] = useState(false);

  // Available themes
  const themeOptions = [
    { value: 'dark', label: 'Dark' },
    { value: 'light', label: 'Light' },
    { value: 'github', label: 'GitHub' },
    { value: '42', label: '42' }
  ];

  // Widget types
  const widgetTypes = [
    { value: 'skills-bars', label: 'Skills Bars' },
    { value: 'skills-radar', label: 'Skills Radar' },
    { value: 'skills-badge', label: 'Skills Badge' }
  ];

  // Generate widget URL
  const generateWidgetUrl = () => {
    if (!username) return '';
    return `${window.location.origin}/api/widget/${widgetType}/${username}?theme=${theme}`;
  };

  // Generate markdown embed code
  const generateEmbedCode = (url) => {
    if (!url) return '';
    return `![42 ${widgetType.replace(/-/g, ' ')}](${url})`;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!username.trim()) {
      setError('Please enter a valid username');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      // First check if the user exists by making an API call
      const response = await fetch(`/api/student/${username}?dataType=skills`);

      if (!response.ok) {
        throw new Error(`Error fetching user data: ${response.statusText}`);
      }

      // Generate the preview URL and embed code
      const url = generateWidgetUrl();
      setPreviewUrl(url);
      setEmbedCode(generateEmbedCode(url));
      setShowPreview(true);

    } catch (err) {
      console.error('Error:', err);
      setError(err.message || 'Error generating widget');
    } finally {
      setIsLoading(false);
    }
  };

  // Copy embed code to clipboard
  const copyToClipboard = () => {
    navigator.clipboard.writeText(embedCode)
      .then(() => {
        alert('Embed code copied to clipboard!');
      })
      .catch(err => {
        console.error('Failed to copy:', err);
      });
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-white">
      <header className="bg-white dark:bg-gray-800 shadow-md">
        <div className="max-w-5xl mx-auto px-4 py-4">
          <h1 className="text-2xl font-bold text-blue-600 dark:text-blue-400">42 Widgets</h1>
        </div>
      </header>

      <main className="max-w-5xl mx-auto p-4 py-8">
        <div className="mb-8 text-center">
          <h2 className="text-3xl font-bold mb-2">Generate Your 42 Widget</h2>
          <p className="text-gray-600 dark:text-gray-300">
            Create beautiful widgets to showcase your 42 school skills on GitHub and other platforms.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {/* Form */}
          <div className="md:col-span-2">
            <form onSubmit={handleSubmit} className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
              <div className="mb-4">
                <label htmlFor="username" className="block text-gray-700 dark:text-gray-300 mb-2 font-medium">
                  42 Intra Username
                </label>
                <input
                  type="text"
                  id="username"
                  className="w-full px-4 py-2 border rounded-md bg-white dark:bg-gray-700 text-gray-800 dark:text-white border-gray-300 dark:border-gray-600"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="e.g. zelhajou"
                  required
                />
              </div>

              <div className="mb-4">
                <label className="block text-gray-700 dark:text-gray-300 mb-2 font-medium">
                  Widget Type
                </label>
                <div className="grid grid-cols-3 gap-2">
                  {widgetTypes.map((type) => (
                    <label 
                      key={type.value}
                      className={`
                        flex items-center justify-center p-2 border rounded-md cursor-pointer text-center
                        ${widgetType === type.value 
                          ? 'bg-blue-50 dark:bg-blue-900 border-blue-500 dark:border-blue-400' 
                          : 'bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600'}
                      `}
                    >
                      <input
                        type="radio"
                        className="sr-only"
                        value={type.value}
                        checked={widgetType === type.value}
                        onChange={() => setWidgetType(type.value)}
                      />
                      <span className={`
                        text-sm font-medium
                        ${widgetType === type.value 
                          ? 'text-blue-600 dark:text-blue-300' 
                          : 'text-gray-700 dark:text-gray-300'}
                      `}>
                        {type.label}
                      </span>
                    </label>
                  ))}
                </div>
              </div>

              <div className="mb-6">
                <label className="block text-gray-700 dark:text-gray-300 mb-2 font-medium">
                  Theme
                </label>
                <div className="grid grid-cols-4 gap-2">
                  {themeOptions.map((themeOption) => (
                    <label 
                      key={themeOption.value}
                      className={`
                        flex items-center justify-center p-2 border rounded-md cursor-pointer text-center
                        ${theme === themeOption.value 
                          ? 'bg-blue-50 dark:bg-blue-900 border-blue-500 dark:border-blue-400' 
                          : 'bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600'}
                      `}
                    >
                      <input
                        type="radio"
                        className="sr-only"
                        value={themeOption.value}
                        checked={theme === themeOption.value}
                        onChange={() => setTheme(themeOption.value)}
                      />
                      <span className={`
                        text-sm font-medium
                        ${theme === themeOption.value 
                          ? 'text-blue-600 dark:text-blue-300' 
                          : 'text-gray-700 dark:text-gray-300'}
                      `}>
                        {themeOption.label}
                      </span>
                    </label>
                  ))}
                </div>
              </div>

              <button
                type="submit"
                className={`
                  w-full py-2 px-4 rounded-md text-white font-medium
                  ${isLoading
                    ? 'bg-gray-500 cursor-not-allowed'
                    : 'bg-blue-600 hover:bg-blue-700'}
                `}
                disabled={isLoading}
              >
                {isLoading ? 'Generating...' : 'Generate Widget'}
              </button>

              {error && (
                <div className="mt-4 text-red-500 dark:text-red-400">
                  {error}
                </div>
              )}
            </form>
          </div>

          {/* Examples or Preview */}
          <div>
            {showPreview ? (
              <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
                <h3 className="text-xl font-bold mb-3">Preview</h3>
                <div className="border border-gray-300 dark:border-gray-600 rounded-lg p-3 overflow-hidden mb-4">
                  <img 
                    src={previewUrl}
                    alt={`${username}'s widget`}
                    className="w-full"
                  />
                </div>

                <h4 className="font-bold mb-2">Embed Code</h4>
                <div className="flex mb-3">
                  <input
                    type="text"
                    className="flex-grow px-3 py-1 text-sm border rounded-l-md bg-gray-50 dark:bg-gray-700 text-gray-800 dark:text-white border-gray-300 dark:border-gray-600"
                    value={embedCode}
                    readOnly
                  />
                  <button
                    onClick={copyToClipboard}
                    className="bg-gray-200 dark:bg-gray-600 text-gray-800 dark:text-white px-3 py-1 rounded-r-md hover:bg-gray-300 dark:hover:bg-gray-500 text-sm"
                    type="button"
                  >
                    Copy
                  </button>
                </div>

                <p className="text-xs text-gray-600 dark:text-gray-400">
                  Add this code to your GitHub README.md file
                </p>
              </div>
            ) : (
              <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
                <h3 className="text-xl font-bold mb-3">Example</h3>
                <div className="border border-gray-300 dark:border-gray-600 rounded-lg p-3 overflow-hidden">
                  <img 
                    src="/api/widget/skills-bars/zelhajou?theme=dark"
                    alt="Example widget"
                    className="w-full"
                  />
                </div>
                <p className="mt-3 text-sm text-gray-600 dark:text-gray-400">
                  Enter your username to generate your custom widget
                </p>
              </div>
            )}
          </div>
        </div>

        {/* How to use */}
        <div className="mt-12 bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
          <h3 className="text-xl font-bold mb-4">How to Use</h3>
          <ol className="list-decimal pl-5 space-y-2 text-gray-700 dark:text-gray-300">
            <li>Enter your 42 intra username</li>
            <li>Choose the widget type you want</li>
            <li>Select a theme that matches your profile style</li>
            <li>Generate the widget</li>
            <li>Copy the markdown code and paste it into your GitHub README.md</li>
          </ol>
        </div>
      </main>

      <footer className="bg-white dark:bg-gray-800 shadow-inner mt-12 py-6">
        <div className="max-w-5xl mx-auto px-4 text-center">
          <p className="text-gray-600 dark:text-gray-400 text-sm">
            Created by <a href="https://github.com/zelhajou" className="text-blue-600 dark:text-blue-400 hover:underline">zelhajou</a> • 
            Not affiliated with 42 School
          </p>
        </div>
      </footer>
    </div>
  );
}

