      ${studentData.displayName || studentData.login}'s Skills Radar
    </text>
  `;

  // Draw axis lines and labels
  displaySkills.forEach((skill, index) => {
    const angle = index * angleStep - Math.PI / 2; // Start from top
    const endX = centerX + radius * Math.cos(angle);
    const endY = centerY + radius * Math.sin(angle);

    // Axis line
    svg += `
      <line 
        x1="${centerX}" 
        y1="${centerY}" 
        x2="${endX}" 
        y2="${endY}" 
        stroke="${theme.colors.border}" 
        stroke-width="1" 
        opacity="0.5" 
      />
    `;

    // Skill label
    const labelX = centerX + (radius + 20) * Math.cos(angle);
    const labelY = centerY + (radius + 20) * Math.sin(angle);

    // Adjust text-anchor based on position
    let textAnchor = 'middle';
    if (labelX < centerX - radius / 2) textAnchor = 'end';
    if (labelX > centerX + radius / 2) textAnchor = 'start';

    svg += `
      <text 
        x="${labelX}" 
        y="${labelY}" 
        font-size="14" 
        fill="${theme.colors.text}" 
        text-anchor="${textAnchor}"
        dominant-baseline="middle"
      >
        ${skill}
      </text>
    `;
  });

  // Draw radar circles
  for (let i = 1; i <= 5; i++) {
    const circleRadius = (radius * i) / 5;

    svg += `
      <circle 
        cx="${centerX}" 
        cy="${centerY}" 
        r="${circleRadius}" 
        fill="none" 
        stroke="${theme.colors.border}" 
        stroke-width="1" 
        opacity="0.3" 
      />
      <text 
        x="${centerX}" 
        y="${centerY - circleRadius}" 
        font-size="12" 
        fill="${theme.colors.textSecondary}" 
        text-anchor="middle" 
        dominant-baseline="middle"
      >
        ${(i * 2)}
      </text>
    `;
  }

  // Draw cursus data polygons
  cursusData.forEach((cursus, cursusIndex) => {
    // Define colors for each cursus
    const colors = [
      { stroke: theme.colors.accent, fill: `${theme.colors.accent}33` },
      { stroke: theme.colors.success, fill: `${theme.colors.success}33` },
      { stroke: theme.colors.warning, fill: `${theme.colors.warning}33` }
    ];

    const cursusColor = colors[cursusIndex % colors.length];

    // Create points for polygon
    const points = displaySkills.map((skill, index) => {
      const skillLevel = cursus.skills[skill] || 0;
      const ratio = skillLevel / 10; // Normalize to 0-1
      const angle = index * angleStep - Math.PI / 2; // Start from top

      const pointX = centerX + radius * ratio * Math.cos(angle);
      const pointY = centerY + radius * ratio * Math.sin(angle);

      return `${pointX},${pointY}`;
    }).join(' ');

    // Draw polygon
    svg += `
      <polygon 
        points="${points}" 
        fill="${cursusColor.fill}" 
        stroke="${cursusColor.stroke}" 
        stroke-width="2" 
      />
    `;

    // Add dots at vertices
    displaySkills.forEach((skill, index) => {
      const skillLevel = cursus.skills[skill] || 0;
      const ratio = skillLevel / 10;
      const angle = index * angleStep - Math.PI / 2;

      const pointX = centerX + radius * ratio * Math.cos(angle);
      const pointY = centerY + radius * ratio * Math.sin(angle);

      svg += `
        <circle 
          cx="${pointX}" 
          cy="${pointY}" 
          r="4" 
          fill="${cursusColor.stroke}" 
        />
      `;
    });
  });

  // Add legend
  const legendY = height - 60;
  cursusData.forEach((cursus, index) => {
    const colors = [
      { stroke: theme.colors.accent, fill: `${theme.colors.accent}33` },
      { stroke: theme.colors.success, fill: `${theme.colors.success}33` },
      { stroke: theme.colors.warning, fill: `${theme.colors.warning}33` }
    ];

    const cursusColor = colors[index % colors.length];
    const legendX = centerX - 200 + index * 200;

    svg += `
      <rect 
        x="${legendX - 10}" 
        y="${legendY - 10}" 
        width="20" 
        height="20" 
        fill="${cursusColor.fill}" 
        stroke="${cursusColor.stroke}" 
        stroke-width="2"
      />
      <text 
        x="${legendX + 20}" 
        y="${legendY}" 
        font-size="14" 
        fill="${theme.colors.text}" 
        dominant-baseline="middle"
      >
        ${cursus.name}
      </text>
    `;
  });

  // Add footer with credit and timestamp
  svg += `
    <text x="${width / 2}" y="${height - 20}" font-size="12" text-anchor="middle" fill="${theme.colors.textSecondary}">
      Generated at ${new Date().toISOString().split('T')[0]} • 42widgets.io
    </text>
  `;

  svg += `</svg>`;

  return svg;
}

