/**
 * Generate skills badge SVG (smaller format for embedding)
 */
export function generateSkillsBadge(studentData, themeName = 'dark', skillName = null) {
  const theme = themes[themeName] || themes.dark;

  // Find the highest skill level across all cursus if no specific skill is requested
  let skillLevel = 0;
  let displaySkillName = 'Average Skill';
  let foundSkill = false;

  if (skillName) {
    // Look for specific skill in all cursus
    Object.values(studentData.skills || {}).forEach(skills => {
      const skill = skills.find(s => s.name.toLowerCase() === skillName.toLowerCase());
      if (skill) {
        skillLevel = skill.level;
        displaySkillName = skill.name;
        foundSkill = true;
      }
    });
  } else {
    // Calculate average of top 3 skills
    const allSkills = [];

    Object.values(studentData.skills || {}).forEach(skills => {
      skills.forEach(skill => {
        allSkills.push(skill);
      });
    });

    // Sort by level in descending order
    allSkills.sort((a, b) => b.level - a.level);

    // Get top 3 or all if less than 3
    const topSkills = allSkills.slice(0, Math.min(3, allSkills.length));

    if (topSkills.length > 0) {
      skillLevel = topSkills.reduce((sum, skill) => sum + skill.level, 0) / topSkills.length;
      foundSkill = true;
    }
  }

  if (!foundSkill) {
    return generateErrorSVG(`Skill "${skillName || 'any'}" not found`, themeName);
  }

  // Badge dimensions
  const width = 250;
  const height = 100;

  // Create badge
  let svg = `
  <svg xmlns="http://www.w3.org/2000/svg" width="${width}" height="${height}" viewBox="0 0 ${width} ${height}">
    <style>
      text {
        font-family: ${theme.fontFamily};
      }
    </style>
    <rect width="${width}" height="${height}" fill="${theme.colors.background}" rx="10" ry="10" />

    <!-- Username -->
    <text x="20" y="30" font-size="16" font-weight="bold" fill="${theme.colors.text}">
      ${studentData.displayName || studentData.login}
    </text>

    <!-- Skill name -->
    <text x="20" y="55" font-size="14" fill="${theme.colors.textSecondary}">
      ${displaySkillName}
    </text>

    <!-- Skill level -->
    <text x="${width - 20}" y="55" font-size="18" font-weight="bold" text-anchor="end" fill="${getSkillColor(skillLevel, themeName)}">
      ${skillLevel.toFixed(2)}/10
    </text>

    <!-- Progress bar -->
    <rect x="20" y="65" width="${width - 40}" height="8" fill="${theme.colors.progressBg}" rx="4" ry="4" />
    <rect x="20" y="65" width="${(width - 40) * skillLevel / 10}" height="8" fill="${getSkillColor(skillLevel, themeName)}" rx="4" ry="4" />

    <!-- Footer -->
    <text x="${width/2}" y="${height - 15}" font-size="10" text-anchor="middle" fill="${theme.colors.textSecondary}">
      42widgets.io
    </text>
  </svg>
  `;

  return svg;
}

