// lib/utils/levelFetcher.js

import axios from 'axios';

// API configuration
const API_URL = process.env.NEXT_PUBLIC_42_API_URL || "https://api.intra.42.fr/v2";
const TOKEN_URL = "https://api.intra.42.fr/oauth/token";
const CLIENT_ID = process.env.FT_CLIENT_ID;
const CLIENT_SECRET = process.env.FT_CLIENT_SECRET;

// Keep the token in memory to avoid unnecessary requests
let cachedToken = null;
let tokenExpiry = null;

/**
 * Get an authentication token from the 42 API
 */
async function getToken() {
  // Check if we have a valid cached token
  if (cachedToken && tokenExpiry && Date.now() < tokenExpiry) {
    return cachedToken;
  }

  try {
    console.log('LevelFetcher: Requesting token from 42 API...');
    const response = await axios.post(TOKEN_URL, {
      grant_type: "client_credentials",
      client_id: CLIENT_ID,
      client_secret: CLIENT_SECRET,
    });

    // Cache the token and set expiry (subtract 5 minutes as safety margin)
    cachedToken = response.data.access_token;
    tokenExpiry = Date.now() + (response.data.expires_in * 1000) - 300000;
    console.log('LevelFetcher: Token obtained successfully');

    return cachedToken;
  } catch (error) {
    console.error('LevelFetcher: Error getting 42 API token:', error);
    throw new Error(`Failed to get 42 API token: ${error.message}`);
  }
}

/**
 * Make an authenticated request to the 42 API
 */
async function api42Request(endpoint, options = {}) {
  try {
    const token = await getToken();

    const response = await axios({
      url: `${API_URL}${endpoint}`,
      headers: { 
        Authorization: `Bearer ${token}`,
        ...options.headers
      },
      ...options
    });

    return response.data;
  } catch (error) {
    console.error(`LevelFetcher: Error in API request ${endpoint}:`, error);
    throw new Error(`Failed API request: ${error.message}`);
  }
}

/**
 * Get user's 42cursus level from the API
 * @param {string} username - 42 username
 * @returns {Promise<number>} - User's level or 0 if not found
 */
export async function getUserLevel(username) {
  try {
    console.log(`LevelFetcher: Fetching level data for ${username}...`);

    // Direct endpoint to get only cursus_users data, which is more efficient
    const endpoint = `/users/${username}/cursus_users`;
    const cursusData = await api42Request(endpoint);

    if (!cursusData || !Array.isArray(cursusData)) {
      console.warn(`LevelFetcher: Invalid data format for ${username}`);
      return 0;
    }

    console.log(`LevelFetcher: Found ${cursusData.length} cursus entries for ${username}`);

    // Look specifically for 42cursus
    const mainCursus = cursusData.find(c => c.cursus?.name === '42cursus');
    if (mainCursus && typeof mainCursus.level === 'number') {
      console.log(`LevelFetcher: 42cursus level found: ${mainCursus.level}`);
      return mainCursus.level;
    }

    // Try with cursus ID 21 (42 cursus)
    const cursusById = cursusData.find(c => c.cursus?.id === 21);
    if (cursusById && typeof cursusById.level === 'number') {
      console.log(`LevelFetcher: Level found via cursus ID 21: ${cursusById.level}`);
      return cursusById.level;
    }

    // If no 42cursus found, use the highest level as fallback
    if (cursusData.length > 0) {
      const highestLevel = Math.max(...cursusData.map(c => c.level || 0));
      console.log(`LevelFetcher: Using highest level: ${highestLevel}`);
      return highestLevel;
    }

    console.warn(`LevelFetcher: No level data found for ${username}`);
    return 0;
  } catch (error) {
    console.error(`LevelFetcher: Error fetching level for ${username}:`, error);
    return 0; // Return 0 as default in case of error
  }
}

export default {
  getUserLevel
};

// lib/utils/projectUtils.js

/**
 * Utilities for working with 42 project data
 */

/**
 * Map of cursus IDs to readable names
 */
export const CURSUS_NAMES = {
    9: 'C Piscine',
    21: 'Common Core',
    67: '42 Events'
  };

  /**
   * Get a human-readable name for a cursus ID
   * @param {number} cursusId - The cursus ID
   * @returns {string} The cursus name
   */
  export function getCursusName(cursusId) {
    return CURSUS_NAMES[cursusId] || `Cursus ${cursusId}`;
  }

  /**
   * Filter projects to get only valid completed ones
   * @param {Array} projects - Array of project objects from API
   * @param {Object} options - Filter options
   * @returns {Array} Filtered projects
   */
  export function getValidatedProjects(projects, options = {}) {
    const { includePiscine = false } = options;

    return projects.filter(project => {
      // Check if project is completed
      const isCompleted = 
        (project.status === 'finished') && 
        (project.validated === true || project['validated?'] === true) &&
        (project.final_mark !== null && project.final_mark > 0);

      // Check if it's a Piscine project
      const isPiscine = project.cursus_ids && project.cursus_ids.includes(9);

      // Include only completed projects, and filter Piscine projects if requested
      return isCompleted && (includePiscine || !isPiscine);
    });
  }

  /**
   * Sort projects by completion date (newest first)
   * @param {Array} projects - Array of project objects
   * @returns {Array} Sorted projects
   */
  export function sortProjectsByDate(projects) {
    return [...projects].sort((a, b) => {
      // Primary sort by marked_at date
      if (a.marked_at && b.marked_at) {
        return new Date(b.marked_at) - new Date(a.marked_at);
      }

      // Secondary sort by updated_at if marked_at not available
      if (a.updated_at && b.updated_at) {
        return new Date(b.updated_at) - new Date(a.updated_at);
      }

      // Tertiary sort by mark (highest first)
      return (b.final_mark || 0) - (a.final_mark || 0);
    });
  }

  /**
   * Prepare project data for display
   * @param {Object} project - Raw project object from API
   * @returns {Object} Processed project object
   */
  export function processProjectForDisplay(project) {
    // Get project name
    const name = project.project ? project.project.name : 'Unknown Project';

    // Get completion date
    const completionDate = project.marked_at || project.updated_at;

    // Get mark
    const mark = project.final_mark || 0;

    // Get team information
    const isTeam = project.teams && 
                  project.teams[0] && 
                  project.teams[0].users && 
                  project.teams[0].users.length > 1;

    const teamSize = isTeam ? project.teams[0].users.length : 1;

    // Get cursus information
    const cursusIds = project.cursus_ids || [];
    const cursusNames = cursusIds.map(id => getCursusName(id));

    // Return processed data
    return {
      id: project.id,
      name,
      mark,
      completionDate,
      isTeam,
      teamSize,
      cursusIds,
      cursusNames,
      status: project.status
    };
  }

  /**
   * Format date in a readable way
   * @param {string} dateString - ISO date string
   * @returns {string} Formatted date
   */
  export function formatDate(dateString) {
    if (!dateString) return 'N/A';

    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return 'N/A';

      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch (error) {
      return 'N/A';
    }
  }

  export default {
    getCursusName,
    getValidatedProjects,
    sortProjectsByDate,
    processProjectForDisplay,
    formatDate
  };

// lib/utils/widgetUrl.js

/**
 * Build a widget URL with specified parameters
 * @param {Object} options - Configuration options for the widget URL
 * @param {string} options.username - 42 username
 * @param {string} options.widgetType - Widget type (skills, projects, etc.)
 * @param {string} options.theme - Widget theme (dark, light)
 * @param {number} options.width - Widget width in pixels
 * @param {number} options.maxSkills - Maximum number of skills to display
 * @param {string} options.baseUrl - Base URL for the widget (defaults to current origin)
 * @returns {string} The complete widget URL
 */
export function buildWidgetUrl({
    username,
    widgetType = 'skills',
    theme = 'dark',
    width,
    maxSkills,
    baseUrl
  }) {
    if (!username) {
      throw new Error('Username is required to build a widget URL');
    }

    // Get base URL (current origin or provided baseUrl)
    const base = baseUrl || (typeof window !== 'undefined' 
      ? window.location.origin 
      : 'https://42widgets.vercel.app');

    // Build the URL - only theme is required parameter
    let url = `${base}/api/widget/${widgetType}/${encodeURIComponent(username)}?theme=${theme}`;

    // Only add optional parameters if explicitly provided and valid
    if (width && !isNaN(width) && width > 0) {
      url += `&width=${width}`;
    }

    // Only add maxSkills if explicitly provided and valid
    // This is crucial - we don't want to accidentally limit skills
    if (maxSkills && !isNaN(maxSkills) && maxSkills > 0) {
      url += `&maxSkills=${maxSkills}`;
    }

    return url;
  }

  /**
   * Generate sharing formats for a widget
   * @param {Object} options - Same options as buildWidgetUrl
   * @returns {Object} Different sharing formats
   */
  export function generateSharingFormats(options) {
    const widgetUrl = buildWidgetUrl(options);
    const { username, widgetType = 'skills' } = options;

    return {
      markdown: `![${username}'s 42 ${widgetType}](${widgetUrl})`,
      html: `<img src="${widgetUrl}" alt="${username}'s 42 ${widgetType}" />`,
      url: widgetUrl,
      bbcode: `[img]${widgetUrl}[/img]`,
      html_linked: `<a href="https://profile.intra.42.fr/users/${username}" target="_blank"><img src="${widgetUrl}" alt="${username}'s 42 ${widgetType}" /></a>`,
      markdown_linked: `[![${username}'s 42 ${widgetType}](${widgetUrl})](https://profile.intra.42.fr/users/${username})`,
    };
  }

  export default {
    buildWidgetUrl,
    generateSharingFormats
  };

